import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Chart from './Chart';
import Table from './reportTable';

function DailyReport({ Editdat }) {

    const [reportdata, setReport] = useState([]);
    const [todayreport, settodayreport] = useState({
      reportdate: "",
      Client: "",
      startEnd: "",
      Totalhrs: "",
      status: "",
    });
    const [editingReport, setEditingReport] = useState(null);
  
    const handlechange = (event) => {
      const { name, value } = event.target;
      settodayreport((prevState) => ({
        ...prevState,
        [name]: value,
      }));
    };
  
    // const submitForm = () => {
    //   if (editingReport) {
    //     editReport(todayreport);
    //   } else {
    //     submitReport(todayreport);
    //   }
    // };
    const submitForm = () => {
      const url = formData.id 
        ? 'http://localhost:8000/testapi/v1/dailyreport/edit' 
        : 'http://localhost:8000/testapi/v1/dailyreport/today';
      const method = formData.id ? 'PUT' : 'POST'; // Use PUT for updates
  
      fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      })
      .then(response => response.json())
      .then(data => {
        console.log('Report submitted successfully:', data);
        if (formData.id) {
          // Update existing report
          setReport(reportData => reportData.map(r => r.id === data.id ? data : r));
          alert('Report is updated');
        } else {
          // Add new report
          setReport(reportData => [...reportData, data]);
          alert('Report is Submitted');
        }
        window.location.reload(); // Reload to reflect changes
      })
      .catch(error => console.error('Error submitting report:', error));
    };
  
  
    useEffect(() => {
      fetch('http://localhost:8000/testapi/v1/dailyreport')
        .then(res => res.json())
        .then(res => setReport(res.data))
        .catch(error => console.error('Error fetching report data:', error));
    }, []);
  
    const submitReport = (todayreport) => {
      fetch('http://localhost:8000/testapi/v1/dailyreport/today', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(todayreport)
      })
        .then(response => response.json())
        .then(data => {
          console.log('Report submitted successfully:', data);
          setReport(reportdata => [...reportdata, data]);
          alert('Report is Submitted');
          settodayreport({
            reportdate: "",
            Client: "",
            startEnd: "",
            Totalhrs: "",
            status: "",
          });
        })
        .catch(error => console.error('Error submitting report:', error));
    };
  
    const editReport = (todayreport) => {
      fetch(`http://localhost:8000/testapi/v1/dailyreport/${editingReport.id}`, {
        method: 'PUT', // Change this if your backend uses POST instead of PUT
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(todayreport)
      })
        .then(response => response.json())
        .then(data => {
          console.log('Report updated successfully:', data);
          setReport(reportdata => reportdata.map(report => report.id === editingReport.id ? data : report));
          alert('Report is Updated');
          setEditingReport(null);
          settodayreport({
            reportdate: "",
            Client: "",
            startEnd: "",
            Totalhrs: "",
            status: "",
          });
        })
        .catch(error => console.error('Error updating report:', error));
    };
  
    const handleEditClick = (report) => {
      setEditingReport(report);
      settodayreport({
        reportdate: report.reportdate,
        Client: report.Client,
        startEnd: report.startEnd,
        Totalhrs: report.Totalhrs,
        status: report.status,
      });
    };

  return (
    <div className="profiled sub-pages">
      <div className="my-appac-hed">
        <div className="myappc-title">
          <p>Daily Report</p>
        </div>
        <div className="breadcrumbs">
          <ul>
            <li>
              <Link to="/myappac">
                Report
                <svg xmlns="http://www.w3.org/2000/svg" width="18px" height="18px" viewBox="0 0 20 20">
                  <path fill="#818798" d="M12.6 12L8 7.4L9.4 6l6 6l-6 6L8 16.6z" />
                </svg>
              </Link>
            </li>
            <li className='present-page'>Daily Report</li>
          </ul>
        </div>
      </div>
      <div className="rpt-wrp">
        <div className='report-frm'>
          <div>
            <p>{Editdat}Report Date</p>
            <input type='date' onChange={handlechange} name="reportdate" id="date" value={todayreport.reportdate} required />
          </div>
          <div>
            <p>Client-name</p>
            <select name="Client" onChange={handlechange} required id="cars" value={todayreport.Client}>
              <option value="Abhifines">Abhifines</option>
              <option value="Carlstahl">Carlstahl</option>
              <option value="Saifoods">Saifoods</option>
              <option value="GKNM">GKNM</option>
            </select>
          </div>
          <div>
            <p></p>
            <ul className='wt-wrp'>
              <li><input type="radio" required name="type" id="wip" value="WIP" onChange={handlechange} checked={todayreport.type === 'WIP'} /><p>WIP</p></li>
              <li><input type="radio" required name="type" id="amc" value="AMC" onChange={handlechange} checked={todayreport.type === 'AMC'} /><p>AMC</p></li>
              <li><input type="radio" required name="type" id="seo" value="SEO" onChange={handlechange} checked={todayreport.type === 'SEO'} /><p>SEO</p></li>
              <li><input type="radio" required name="type" id="others" value="Others" onChange={handlechange} checked={todayreport.type === 'Others'} /><p>Others</p></li>
            </ul>
          </div>
          <div>
            <p>Project Description</p>
            <textarea required onChange={handlechange} name='description' value={todayreport.description}></textarea>
          </div>
          <div className='start-end'>
            <div>
              <p>Start Time</p>
              <input type='time' required onChange={handlechange} name='startEnd' value={todayreport.startEnd}></input>
            </div>
            <div>
              <p>End Time</p>
              <input type='time' required onChange={handlechange} name='Totalhrs' value={todayreport.Totalhrs}></input>
            </div>
          </div>
          <div>
            <p>Status</p>
            <textarea className='description' onChange={handlechange} name='status' value={todayreport.status}></textarea>
          </div>
          <div className="leave-bt-wrp">
          <button type="button" onClick={submitForm} className="submit">{editingReport ? 'Update' : 'Submit'}</button>
          <button type="button" onClick={() => settodayreport({ reportdate: "", Client: "", startEnd: "", Totalhrs: "", status: "" })} className="clear">Clear</button>
          </div>
        </div>
        <div className='report-sts'>
          <div>
            <Chart />
          </div>
          <br />
          <div>
            <div className="w-table-cwrp">
              <div className="serach-box">
                <div className="twrap">
                  <select className="total-box" name="recors" id="records">
                    <option value="10">10</option>
                    <option value="15">15</option>
                    <option value="20">20</option>
                    <option value="All">All</option>
                  </select>
                  <label htmlFor="cars">records per page</label>
                </div>
                <search>
                  <form>
                    <label>Search :</label>
                    <input className="search-box" name="fsrch" id="fsrch" placeholder=" " />
                  </form>
                </search>
              </div>
              <div className="works-table">
                <table>
                  <thead>
                    <tr>
                      <th>Sno.</th>
                      <th>Report Date</th>
                      <th>Client</th>
                      <th>Start /End Time</th>
                      <th>Total Hours</th>
                      <th>Status</th>
                      <th>Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {reportdata.map((data, index) => (
                      <Table key={index} reportdata={data} onEdit={handleEditClick} />
                    ))}
                  </tbody>
                </table>
                <div className="serach-box">
                  <div><p>Showing 1 to 1 of 1 entries</p></div>
                  <div className='pagination-wrp'>
                    <div className='pg-arr'>
                      <svg xmlns="http://www.w3.org/2000/svg" width="20px" height="20px" viewBox="0 0 24 24">
                        <path fill="none" stroke="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="1.5" d="m14 7l-5 5l5 5" />
                      </svg>
                    </div>
                    <div><a className="pg-nav active">1</a></div>
                    <div><a className="pg-nav">2</a></div>
                    <div><a className="pg-nav">3</a></div>
                    <div className='pg-arr'>
                      <svg xmlns="http://www.w3.org/2000/svg" width="20px" height="20px" viewBox="0 0 24 24">
                        <path fill="none" stroke="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="1.5" d="m10 17l5-5l-5-5" />
                      </svg>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default DailyReport;
