import React, { useState } from 'react';
import Header from './component/header';
import SideMenu from './component/sidemenu';
import Login from './component/Login';  // Import the Login component

function Main() {
  const [isLoginVisible, setLoginVisible] = useState(true);

  // Function to handle the login button click
  function handleLogin() {
    // Here you would typically validate credentials (e.g., call an API)

    // Hide the Login component
    setLoginVisible(false);

    // Redirect to the dashboard or perform other actions
    window.location.href = '/dashboard';  // Replace with your dashboard route
  }

  return (
    <div>
      <Header />
      <SideMenu />
      
      {/* Conditionally render the Login component */}
      {isLoginVisible && <Login onLogin={handleLogin} />}
    </div>
  );
}

export default Main;
