import React, { useState, useEffect } from 'react';


function Login({ isVisible }) {
  const [currentTime, setCurrentTime] = useState(new Date());
  const [user , setUserdata] = useState({
    username:"",
      password:""
  })
  const empid = user.username;  // Assuming username is empid in your logic
const password = user.password;

const onLogin = () => {
    fetch('http://localhost:8000/testapi/v1/login', {
        method: 'PUT',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            empid,  // Sending empid (which is username in your case)
            password,
          

        })
    })
    .then((res) => res.json())  // Parse JSON response
    .then((data) => {
        if (data && data.success) {
          
            // Check if the user's empid matches and the password is correct
            if (data.empid === empid && data.password === password) {
                alert('User is present, login successful');
            } else {
                alert('Invalid credentials');
            }
        } else {
            alert('User not found or incorrect credentials');
        }
    })
    .catch(error => {
        console.error(`Error fetching data:'  ${error}`)
    })
  }
  // Update the current time every second
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    // Cleanup the interval on component unmount
    return () => clearInterval(timer);
  }, []);

  const hours = currentTime.getHours();
  const min = currentTime.getMinutes();
  const sec = currentTime.getSeconds();
  
  const onInputchange=(event)=>{
    const { name, value, type, checked } = event.target;
    setUserdata(()=> ({
      [name]: type === 'checkbox' ? checked : value,

    }));
  }

  // Conditional styling based on visibility
  const loginStyle = {
    display: isVisible ? 'block' : 'none',
    zIndex: isVisible ? '100' : '0',  // Set z-index to 100 when visible, 0 when hidden
  };

  return (
    <div className='loginPage' style={loginStyle}>
      <div className="login-div">
        <p className='currentTime'>{`${hours} : ${min < 10 ? '0' + min : min} : ${sec < 10 ? '0' + sec : sec}`}</p>
        <p className='currentday'>Saturday August 31 2024</p>
        <img src='https://appacmedia.com/images/appac-logo-new.svg' alt="Logo"/>
        <p className='wel-hed'>WELCOME TO NEW BETA VERSION</p>

        <input 
          type='text' 
          name='username'
          aria-required 
          placeholder='username' 
          id="username"
          onBeforeInput={onInputchange}
          required
        /><br />

        <input 
          type='password' 
          name="password"
          placeholder='Password' 
          id="password"
          onChange={onInputchange}
          required
        />

        <p className='for-pass'>
          <button>Forgot Password?</button>
        </p>

        <button to='/'
          className='loginbutton' 
          onClick={onLogin}  // Trigger the function passed from the Main component
        >
          Login
        </button>
      </div>
    </div>
  );
}

export default Login;
