import React, { useState, useEffect } from 'react';
import { Navigate } from 'react-router-dom';

function Login({ isVisible }) {
  const [currentTime, setCurrentTime] = useState(new Date());
  const [user, setUserdata] = useState({
    username: "",
    password: ""
  });
  const empid = user.username;  // Assuming username is empid in your logic
  const password = user.password;
  const navigate = Na
  // Correct login function for PUT request
  const onLogin = () => {
    fetch('http://localhost:8000/testapi/v1/login', {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        empid,  // Sending empid (which is username in your case)
        password,
      })
    })
    .then((res) => res.json())  // Parse JSON response
    .then((data) => {
      console.log(empid);
      if (data && data.success) {
        // Check if the user's empid matches and the password is correct
        if (data.empid === empid && data.password === password) {
          alert('User is present, login successful');
          navigate('/');
        } else {
          alert('Invalid credentials');
        }
      } else {
        alert('User not found or incorrect credentials');
      }
    })
    .catch(error => {
      console.error(`Error fetching data: ${error}`);
    });
  };

  // Update the current time every second
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    // Cleanup the interval on component unmount
    return () => clearInterval(timer);
  }, []);

  // Format the current time
  const hours = currentTime.getHours();
  const min = currentTime.getMinutes();
  const sec = currentTime.getSeconds();

  // Handle input change
  const onInputchange = (event) => {
    const { name, value, type, checked } = event.target;
    setUserdata((prevstate) => ({
      ...prevstate,  // Correctly update only the changed field
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  // Conditional styling based on visibility
  const loginStyle = {
    display: isVisible ? 'block' : 'none',
    zIndex: isVisible ? 100 : 0,  // Set z-index to 100 when visible, 0 when hidden
  };

  // Dynamically display current date
  const currentDay = currentTime.toLocaleDateString('en-US', { weekday: 'long', month: 'long', day: 'numeric', year: 'numeric' });

  return (
    <div className='loginPage' style={loginStyle}>
      <div className="login-div">
        <p className='currentTime'>{`${hours} : ${min < 10 ? '0' + min : min} : ${sec < 10 ? '0' + sec : sec}`}</p>
        <p className='currentday'>{currentDay}</p>  {/* Display current day dynamically */}
        <img src='https://appacmedia.com/images/appac-logo-new.svg' alt="Logo"/>
        <p className='wel-hed'>WELCOME TO NEW BETA VERSION</p>

        <input 
          type='text' 
          name='username'
          value={user.username}
          aria-required 
          placeholder='Username' 
          id="username"
          onChange={onInputchange}  // Corrected to onChange event
          required
        /><br />

        <input 
          type='password' 
          name="password"
          value={user.password}
          placeholder='Password' 
          id="password"
          onChange={onInputchange}  // Corrected to onChange event
          required
        />

        <p className='for-pass'>
          <button>Forgot Password?</button>
        </p>

        <button
          className='loginbutton' 
          onClick={onLogin}  // Trigger the login function
        >
          Login
        </button>
      </div>
    </div>
  );
}

export default Login;
