import React ,{useState ,useEffect} from 'react'


function Login() {
  const [currentTime, setCurrentTime] = useState(new Date());
  function handleLogin() {
    const username = document.getElementById('username').value;
    const password = document.getElementById('password').value;

    if (!username || !password) {
        alert('Both fields are required!');
        return;
    }

    // Redirect to the dashboard if both fields are filled
    window.location.href = '/dashboard';  // replace with the actual dashboard link
}

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    // Cleanup the interval on component unmount
    return () => clearInterval(timer);
  }, []);

  const hours = currentTime.getHours();
  const min = currentTime.getMinutes();
  const sec = currentTime.getSeconds();

  return (
    <div className='loginPage'>
        <div className="login-div">
    <p className='currentTime'>{`${hours} : ${min < 10 ? '0' + min : min} : ${sec < 10 ? '0' + sec : sec}`}</p>
    <p className='currentday'>Saturday August 31 2024</p>
    <img src='https://appacmedia.com/images/appac-logo-new.svg'/>
    <p className='wel-hed'>WELCOME TO NEW BETA VERSION</p>

    <input 
        type='text' 
        aria-required 
        placeholder='Username' 
        id="username"
        required
    /><br></br>

    <input 
        type='password' 
        placeholder='Password' 
        id="password"
        required
    />

    <p className='for-pass'>
        <a href='#'>Forgot Password ?</a>
    </p>

    <button 
        className='loginbutton' 
        onClick={handleLogin}
    >
        Login
    </button>
</div>

    </div>
  )
}

export default Login
