import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Chart from './Chart';
import ReportTable from './reportTable';

function DailyReport() {
  const [reportData, setReport] = useState([]);
  const [formData, setFormData] = useState({
    report_date: "",
    report_date1: "",
    client: "",
    start_time: "",
    end_time: "",
    start:"",
    status: "",
    enquiry_month:"",
    id: "" // Added id for editing
  });

  const [isEditing, setIsEditing] = useState(false); // Track if editing

  // Pagination state
  const [recordsPerPage, setRecordsPerPage] = useState(10); // Default value is 10 records per page
  const [currentPage, setCurrentPage] = useState(1); // Start with the first page

  // Handle input changes
  const handleChange = (event) => {
    const { name, value, type, checked } = event.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  // Submit form data
  const submitForm = () => {
    const startHour = formData.start.split(':')[0];
    const startMinutes = formData.start.split(':')[1];
    const report_date1 = formData.report_date.split('-').reverse().join('-');
    const endHour = formData.end.split(':')[0];
    const endMinutes = formData.end.split(':')[1];
    
    const formatTime = (hour, minutes) => {
        const period = hour >= 12 ? 'PM' : 'AM';
        const adjustedHour = hour % 12 || 12;
        return `${adjustedHour}:${minutes} ${period}`;
    };
    
    const start_time = formatTime(parseInt(startHour), startMinutes);
    const end_time = formatTime(parseInt(endHour), endMinutes);

    const w_hours = endHour - startHour;
    const w_mins = startMinutes - endMinutes;

    const time = new Date();
    const submit_time = time.toLocaleString();
    const monthy = new Date();
    const enquiry_month = `${monthy.getMonth() + 1}-${monthy.getFullYear()}`;

    const updatedFormData = {
      ...formData,
      submit_time,
      start_time,
      end_time,
      w_hours,
      w_mins,
      enquiry_month,
      report_date1,
    };

    const url = formData._id
      ? `http://localhost:8000/testapi/v1/dailyreport/edit/${formData._id}`
      : 'http://localhost:8000/testapi/v1/dailyreport/today';
    const method = formData._id ? 'PUT' : 'POST';
  
    fetch(url, {
      method,
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedFormData),
    })
      .then(response => response.json())
      .then(body => {
        if (formData._id) {
          setReport(reportData =>
            reportData.map(r => r._id === formData._id ? { ...body, report_date1, submit_time, start_time, end_time, w_hours, w_mins, enquiry_month } : r)
          );
          alert('Report updated successfully');
        } else {
          setReport(reportData => [...reportData, { ...body, report_date1, submit_time, start_time, end_time, w_hours, w_mins, enquiry_month }]);
          alert('Report submitted successfully');
        }

        setFormData({
          report_date: "",
          report_date1: "",
          client: "",
          start: "",
          end: "",
          start_time: "",
          end_time: "",
          enquiry_month: "",
          status: "",
          w_hours: "",
          w_mins: "",
          _id: ""
        });
        setIsEditing(false);
      })
      .catch(error => {
        console.error('Error submitting report:', error);
        alert('There was an error submitting the report');
      });
  };

  useEffect(() => {
    fetch('http://localhost:8000/testapi/v1/dailyreport')
      .then(res => res.json())
      .then(res => setReport(res.data))
      .catch(error => console.error('Error fetching report data:', error));
  }, []);

  const onEdit = (report) => {
    setFormData({
      ...report
    });
    setIsEditing(true); // Set editing state to true
  };

  const clearForm = () => {
    setFormData({
      report_date: "",
      client: "",
      start_time: "",
      end_time: "",
      status: "",
      id: ""
    });
    setIsEditing(false);
  };

  // Pagination logic
  const totalPages = Math.ceil(reportData.length / recordsPerPage);
  const indexOfLastItem = currentPage * recordsPerPage;
  const indexOfFirstItem = indexOfLastItem - recordsPerPage;
  const currentData = reportData.slice(indexOfFirstItem, indexOfLastItem);

  const handlePageChange = (newPage) => {
    if (newPage > 0 && newPage <= totalPages) {
      setCurrentPage(newPage);
    }
  };

  const handleRecordsPerPageChange = (event) => {
    const value = event.target.value === "All" ? reportData.length : parseInt(event.target.value);
    setRecordsPerPage(value);
    setCurrentPage(1); // Reset to the first page when changing records per page
  };

  return (
    <div className="profiled sub-pages">
      <div className="my-appac-hed">
        <div className="myappc-title">
          <p>Daily Report</p>
        </div>
        <div className="breadcrumbs">
          <ul>
            <li>
              <Link to="/myappac">
                Report
                <svg xmlns="http://www.w3.org/2000/svg" width="18px" height="18px" viewBox="0 0 20 20">
                  <path fill="#818798" d="M12.6 12L8 7.4L9.4 6l6 6l-6 6L8 16.6z" />
                </svg>
              </Link>
            </li>
            <li className='present-page'>Daily Report</li>
          </ul>
        </div>
      </div>
      <div className="rpt-wrp">
        <div className='report-frm'>
          {/* Form input fields remain unchanged */}
          <button type="button" onClick={submitForm} className="submit">
            {isEditing ? 'Update' : 'Submit'}
          </button>
          <button type="button" onClick={clearForm} className="clear">
            Clear
          </button>
        </div>
        <div className='report-sts'>
          <div>
            <Chart />
          </div>
          <br />
          <div>
            <div className="w-table-cwrp">
              <div className="serach-box">
                <div className="twrap">
                  <select className="total-box" name="records" id="records" onChange={handleRecordsPerPageChange}>
                    <option value="10">10</option>
                    <option value="15">15</option>
                    <option value="20">20</option>
                    <option value="All">All</option>
                  </select>
                  <label htmlFor="records">Records per page</label>
                </div>
                <search>
                  <form>
                    <label>Search :</label>
                    <input className="search-box" name="fsrch" id="fsrch" placeholder=" " />
                  </form>
                </search>
              </div>
              <div className="works-table">
                <table>
                  <thead>
                    <tr>
                      <th>Sno.</th>
                      <th>Report Date</th>
                      <th>Client</th>
                      <th>Start / End Time</th>
                      <th>Total Hours</th>
                      <th>Status</th>
                      <th>Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {currentData.map((data, index) => (
                      <ReportTable
                        key={data._id}
                        index={index + indexOfFirstItem + 1} // Adjust Sno. based on pagination
                        reportdata={data}
                        onEdit={onEdit}
                      />
                    ))}
                  </tbody>
                </table>
                <div className="serach-box">
                  <div>
                    <p>
                      Showing {indexOfFirstItem + 1} to {Math.min(indexOfLastItem, reportData.length)} of {reportData.length} entries
                    </p>
                  </div>
                  <div className="twrap">
                    <button onClick={() => handlePageChange(currentPage - 1)} disabled={currentPage === 1}>Previous</button>
                    <button onClick={() => handlePageChange(currentPage + 1)} disabled={currentPage === totalPages}>Next</button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default DailyReport;
